import React from "react";
import NoteContext from "./noteContext";
import { useState } from "react";

const NoteState = (props) => {
  const host = "http://localhost:5000";
  const notesInitial = [
    {
      _id: "66ddede127b093a1b3c645f9",
      user: "66dc29071c5e6552da31fffc",
      title: "My title",
      description: "Please wake up early",
      tag: "personal",
      data: "2024-09-08T18:33:05.299Z",
      __v: 0,
    },
    {
      _id: "66df0b61113d44085a1edc4a",
      user: "66dc29071c5e6552da31fffc",
      title: "My title update2",
      description: "Please wake up early update3",
      tag: "personal",
      data: "2024-09-09T14:51:13.708Z",
      __v: 0,
    },
    {
      _id: "66df13800561158cda4e159a",
      user: "66dc29071c5e6552da31fffc",
      title: "My title",
      description: "Please wake up early",
      tag: "personal",
      data: "2024-09-09T15:25:52.185Z",
      __v: 0,
    },
    {
      _id: "66e1dbf8a8bdcfa572375289",
      user: "66dc29071c5e6552da31fffc",
      title: "mamu",
      description: "go outside",
      tag: "area",
      data: "2024-09-11T18:05:44.115Z",
      __v: 0,
    },
  ];

  const [notes, setNotes] = useState(notesInitial);

  // Add a Notes

  const addNotes = async (title, description, tag) => {
    //API call

    const response = await fetch(`${host}/api/notes/addnotes`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZkYzI5MDcxYzVlNjU1MmRhMzFmZmZjIn0sImlhdCI6MTcyNTcxMDUxM30.g-tXKSvnSm7hun1SzY_gP3sLVMUSLSKSszsLuw-hsUc",
      },

      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();
    console.log(json)
    const note = {
      _id: "66df13800561158cda4e159a",
      user: "66dc29071c5e6552da31fffc",
      title: title,
      description: description,
      tag: tag,
      data: "2024-09-09T15:25:52.185Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  // Delete a Note
  const deleteNotes = (id) => {
    console.log("Deleting the note with id", id);
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  // Edit a Notes
  const editNotes = async (id, title, description, tag) => {
    //API call

    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjZkYzI5MDcxYzVlNjU1MmRhMzFmZmZjIn0sImlhdCI6MTcyNTcxMDUxM30.g-tXKSvnSm7hun1SzY_gP3sLVMUSLSKSszsLuw-hsUc",
      },

      body: JSON.stringify({ title, description, tag }),
    });
    const json = response.json();
    // logic to edit in clint
    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  return (
    <NoteContext.Provider value={{ notes, addNotes, deleteNotes, editNotes }}>
      {props.children}
    </NoteContext.Provider>
  );
};

export default NoteState;
