import React from "react";
import icon1 from "../gym/assets/icon-1.png";
import icon2 from "../gym/assets/icon-2.png";
import icon3 from "../gym/assets/icon-3.png";
import icon4 from "../gym/assets/icon-1.png";

const HeroCards = () => {
  const cards = [
    { icon: "../gym/assets/icon-1.png", title: "Healthy Lifestyle", description: "Embrace a healthy lifestyle..." },
    { icon: "../gym/assets/icon-2.png", title: "Body & Mind Balance", description: "Cultivate a strong, flexible body..." },
    { icon: "../gym/assets/icon-3.png", title: "Meditation Practice", description: "Discover inner serenity..." },
    { icon: "../gym/assets/icon-4.png", title: "Self-Care", description: "Transformative power of self-care..." }
  ];

  return (
    <section className="hero" id="hero">
      <div className="section__container hero__container">
        {cards.map((card, index) => (
          <div className="hero__card" key={index}>
            <span><img src={card.icon} alt={card.title} /></span>
            <h4>{card.title}</h4>
            <p>{card.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default HeroCards;
