import React from "react";

const Alert = (props) => {
  const capitalize = (word)=>{
    const lower = word.toLowerCase();
    return lower.chatAt(0).toUpperCase()+lower.slice(1);
  }
  return (
    <div>
      <div className="alert alert-primary" role="alert">
        {props.alert}
      </div>
    </div>
  );
};

export default Alert;
