import React from 'react';
import stories1 from '../gym/assets/stories-1.jpg';
import stories2 from '../gym/assets/stories-2.jpg';
import stories3 from '../gym/assets/stories-3.jpg';


const Stories = () => {
  const stories = [
    {
      image: stories1,
      name: 'Emily Williams',
      role: 'Trainer',
      testimony: "This yoga website has been a game-changer for me. The variety of classes and guided sessions have not only improved my flexibility but also brought a sense of calm to my hectic days."
    },
    {
      image: stories2,
      name: 'Ava Johnson',
      role: 'Member',
      testimony: "The tailored sessions and expert guidance have not only eased my discomfort but also boosted my overall well-being. A fantastic resource for both beginners and experienced yogis."
    },
    {
      image: stories3,
      name: 'Sophia Smith',
      role: 'Member',
      testimony: "The on-demand classes allow me to practice whenever I can, and the mindfulness exercises have brought a new level of clarity to my life. A must-visit for anyone seeking holistic wellness."
    }
  ];

  return (
    <section className="section__container stories__container" id="stories">
      <p className="section__subheader">TESTIMONY</p>
      <h2 className="section__header">Successful Stories</h2>
      <div className="stories__grid">
        {stories.map((story, index) => (
          <div className="stories__card" key={index}>
            <div className="stories__content">
              <span><i className="ri-double-quotes-l"></i></span>
              <p>{story.testimony}</p>
            </div>
            <div className="stories__author">
              <img src={story.image} alt="author" />
              <div className="stories__author__details">
                <h4>{story.name}</h4>
                <h6>{story.role}</h6>
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Stories;
